import 'package:angular/angular.dart';import 'package:angular/routing/module.dart';import 'package:angulartdart_todo/component/todo_detail_component.dart';import 'package:angulartdart_todo/service/query_service.dart';import 'package:angulartdart_todo/controller/todo_list_controller.dart';import 'package:angulartdart_todo/routing/todo_router.dart';class TodoModule extends Module {  TodoModule() {    type(TodoDetailComponent);    type(QueryService);    type(TodoListController);    type(RouteInitializer, implementedBy: TodoRouteInitializer);    factory(NgRoutingUsePushState, (_) => new NgRoutingUsePushState.value(false));  }}main() {  ngBootstrap(module: new TodoModule());}