library todo_delete_controller;import 'package:angular/angular.dart';import 'package:angular/routing/module.dart';import 'package:angulartdart_todo/model/item.dart';import 'package:angulartdart_todo/service/query_service.dart';@NgController(  selector: '[delete-controller]',  publishAs: 'ctrl')class TodoDeleteController {  Item todo;  RouteProvider _routeProvider;  QueryService _queryService;  Router _router;  TodoDeleteController(RouteProvider this._routeProvider,                       QueryService this._queryService,                       Router this._router){    var todoId = _routeProvider.parameters['todoId'];    todo = _queryService.getById(todoId);  }  delete() {    _queryService.deleteItem(todo.id);    _router.go("list", {});  }  cancel() {    _router.go('todo.detail', {'todoId': todo.id});  }}