library todo_detail_controller.dart;import 'package:angular/angular.dart';import 'package:angular/routing/module.dart';import 'package:angulartdart_todo/service/query_service.dart';@NgController(  selector: '[detail-controller]',  publishAs: 'ctrl')class TodoDetailController {  Item todo;  RouteProvider _routeProvider;  QueryService _queryService;  TodoDetailController(RouteProvider this._routeProvider,                       QueryService this._queryService) {    var todoId = _routeProvider.parameters['todoId'];    todo = _queryService.getById(todoId);  }}