library todo_list_controller.dart;import 'package:angular/angular.dart';import 'package:angulartdart_todo/service/query_service.dart';@NgController(  selector: '[list-controller]',  publishAs: 'ctrl')class TodoListController {  QueryService _queryService;  Map _items;  get items => _items;  TodoListController(QueryService this._queryService) {    _items = _queryService.getAllItems();  }}