library todo_router.dart;import 'package:angular/angular.dart';class TodoRouteInitializer extends RouteInitializer {  init(Router router, ViewFactory view) {    router.root      ..addRoute(        name: 'list',        path: '/list',        enter: view('view/todoList.html'),        defaultRoute: true      )      ..addRoute(        name: 'create',        path: '/create',        enter: view('view/todoCreate.html')      )      ..addRoute(        name: 'todo',        path: '/todo/:todoId',        mount: (Route route) => route          ..addRoute(            name: 'update',            path: '/update',            enter: view('view/todoUpdate.html')          )          ..addRoute(            name: 'detail',            path: '/detail',            enter: view('view/todoDetail.html')          )          ..addRoute(            name: 'delete',            path: '/delete',            enter: view('view/todoDelete.html')          )    );  }}