library flash_message_service;class FlashMessageService {  String SAVE_MESSAGE = "Todo: {newItem.text}, was successfully saved!";  String UPDATE_MESSAGE = "Todo: {newItem.text}, was successfully updated!";  Map<String, Message> _messagesCache;  get messages => _messagesCache.values;  FlashMessageService() {    _messagesCache = new Map();  }  addMessage(message) {    _messagesCache[message.id] = message;  }  removeMessage(message) {    _messagesCache.remove(message.id);  }}