library query_service.dart;import 'package:angulartdart_todo/model/item.dart';class QueryService {  Map<String, Item> _itemsCache;  loadItems() {    List<Item> items;    items = [        new Item('1', 'run 10k nike', false),        new Item('2', 'run 10k adidas', false)    ];    _itemsCache = new Map();    for(var item in items) {      _itemsCache[item.id] = item;    }  }  getAllItems() {    if(_itemsCache == null) {      loadItems();    }    return _itemsCache.values;  }  createItem(item) {    if(_itemsCache == null) {      loadItems();    }    _itemsCache[item.id] = item;  }  getById(itemId) {    if(_itemsCache == null) {      loadItems();    }    return _itemsCache[itemId];  }  deleteItem(itemId) {    if(_itemsCache == null) {      loadItems();    }    _itemsCache.remove(itemId);  }}